    return 0;
}
</code></pre>  </li> <li> <p>事实上 \(\mathcal O(2^knk\log)\) 或 \(\mathcal O(2^k nk\alpha)\) 的部分可以进一步优化成 \(\mathcal O(2^kn\log)\) 或 \(\mathcal O(2^k n\alpha)\)。</p> <p>设改造 \(S\) 中的城镇。对每个 \(S\)，记录 \(S\) 对应新图的 MST 的边 \(\text{MST}(S)\)。</p> <p>\(\text{MST}(S)\) 可以由【\(\text{MST}(S-\text{lowbit}(S))\) 的 \(\mathcal O(n)\) 条边】和【\(\text{lowbit}(S)\) 这个城镇连出去的 \(n\) 条边】排序/归并得到。</p>  100 分（直接排序） <pre><code>#include&lt;bits/stdc++.h&gt;
using namespace std;
const int N=1e4+15,M=1e6+5,K=20,S=1&lt;&lt;10;
int n,m,k,t,f[N],c[K],w[K][N],num[S];
long long sum,ans;
struct E{
    int x,y,z;
}e[M],b[N&lt;&lt;1],a[S][N];
int find(int x){return x==f[x]?x:f[x]=find(f[x]);}
signed main(){
    scanf("%d%d%d",&amp;n,&amp;m,&amp;k);
    for(int i=1;i&lt;=m;i++)
        scanf("%d%d%d",&amp;e[i].x,&amp;e[i].y,&amp;e[i].z);
    sort(e+1,e+1+m,[](E x,E y){return x.z&lt;y.z;});
    iota(f+1,f+1+n,1);
    for(int i=1;i&lt;=m;i++){
        int x=find(e[i].x),y=find(e[i].y);
        if(x!=y) f[y]=x,a[0][++t]=e[i],sum+=e[i].z;
    }
    num[0]=t,ans=sum;
    for(int i=1;i&lt;=k;i++){
        scanf("%d",&amp;c[i]);
        for(int j=1;j&lt;=n;j++) scanf("%d",&amp;w[i][j]);
    }
    for(int s=1;s&lt;(1&lt;&lt;k);s++){
        int x=__lg(s&amp;(-s))+1,s1=s^(s&amp;(-s)),cnt=num[s1];
        copy(a[s1]+1,a[s1]+1+cnt,b+1);
        for(int i=1;i&lt;=n;i++) b[++cnt]={x+n,i,w[x][i]};
        sort(b+1,b+1+cnt,[](E x,E y){return x.z&lt;y.z;});
        iota(f+1,f+1+n+k,1),sum=0;
        for(int i=1;i&lt;=k;i++)
            if(s&gt;&gt;(i-1)&amp;1) sum+=c[i];
        t=0;
        for(int i=1;i&lt;=cnt;i++){
            int x=find(b[i].x),y=find(b[i].y);
            if(x!=y) f[y]=x,sum+=b[i].z,a[s][++t]=b[i];
        }
        num[s]=t,ans=min(ans,sum);
    }
    printf("%lld\n",ans);
    return 0;
}
</code></pre>   100 分（归并排序+按秩合并） <pre><code>#include&lt;bits/stdc++.h&gt;
using namespace std;
const int N=1e4+15,M=1e6+5,K=20,S=1&lt;&lt;10;
int n,m,k,t,f[N],c[K],num[S],sz[N];
long long sum,ans;
struct E{
    int x,y,z;
}e[M],b[N&lt;&lt;1],w[K][N],a[S][N];
int find(int x){return x==f[x]?x:f[x]=find(f[x]);}
void merge(int x,int y){
    if(sz[x]&lt;sz[y]) swap(x,y);
    f[y]=x,sz[x]+=sz[y];
}
signed main(){
    scanf("%d%d%d",&amp;n,&amp;m,&amp;k);
    for(int i=1;i&lt;=m;i++)
        scanf("%d%d%d",&amp;e[i].x,&amp;e[i].y,&amp;e[i].z);
    sort(e+1,e+1+m,[](E x,E y){return x.z&lt;y.z;});
    for(int i=1;i&lt;=n;i++) f[i]=i,sz[i]=1;
    for(int i=1;i&lt;=m;i++){
        int x=find(e[i].x),y=find(e[i].y);
        if(x!=y) merge(x,y),a[0][++t]=e[i],sum+=e[i].z;
    }
    num[0]=t,ans=sum;
    for(int i=1;i&lt;=k;i++){
        scanf("%d",&amp;c[i]);
        for(int j=1,x;j&lt;=n;j++) scanf("%d",&amp;x),w[i][j]={i+n,j,x};
        sort(w[i]+1,w[i]+1+n,[](E x,E y){return x.z&lt;y.z;});
    }
    for(int s=1;s&lt;(1&lt;&lt;k);s++){
        int x=__lg(s&amp;(-s))+1,s1=s^(s&amp;(-s)),cnt=num[s1]+n;
        for(int i=1,j=1,p=1;p&lt;=cnt;p++){
            if((i&lt;=num[s1]&amp;&amp;a[s1][i].z&lt;=w[x][j].z)||j&gt;n) b[p]=a[s1][i++];
            else b[p]=w[x][j++];
        }
        sort(b+1,b+1+cnt,[](E x,E y){return x.z&lt;y.z;}); 
        for(int i=1;i&lt;=n+k;i++) f[i]=i,sz[i]=1;
        sum=0;
        for(int i=1;i&lt;=k;i++)
            if(s&gt;&gt;(i-1)&amp;1) sum+=c[i];
        t=0;
        for(int i=1;i&lt;=cnt;i++){
            int x=find(b[i].x),y=find(b[i].y);
            if(x!=y) merge(x,y),sum+=b[i].z,a[s][++t]=b[i];
        }
        num[s]=t,ans=min(ans,sum);
    }
    printf("%lld\n",ans);
    return 0;
}
</code></pre>  </li>
